srt <- function(dat){
    # returns data with labels for raw SRT vars added

    dat <- within(dat, {

        # Labels for raw variables, from autogenerated R file
        label(recency)="Recency"
        label(learning.consistency)="Learning Consistency"
        label(retroactive.t1to6)="Retroactive Interference Trials 1-6"
        label(retroactive.cued.recall)="Retroactive Interference Cued Recall"
        label(retroactive.delayed.recall)="Retroactive Interference Delayed Recall"
        label(bowl.total)="Bowl Total"
        label(bowl.cued.recall)="Bowl on Cued Recall"
        label(bowl.delayed.recall)="Bowl on Delayed Recall"
        label(bowl.recognition)="Bowl on Delayed Recognition"
        label(passion.total)="Passion Total"
        label(passion.cued.recall)="Passion on Cued Recall"
        label(passion.delayed.recall)="Passion on Delayed Recall"
        label(passion.recognition)="Passion on Delayed Recognition"
        label(dawn.total)="Dawn Total"
        label(dawn.cued.recall)="Dawn on Cued Recall"
        label(dawn.delayed.recall)="Dawn on Delayed Recall"
        label(dawn.recognition)="Dawn on Delayed Recognition"
        label(judgment.total)="Judgment Total"
        label(judgment.cued.recall)="Judgment on Cued Recall"
        label(judgment.delayed.recall)="Judgment on Delayed Recall"
        label(judgment.recognition)="Judgment on Delayed Recognition"
        label(grant.total)="Grant Total"
        label(grant.cued.recall)="Grant on Cued Recall"
        label(grant.delayed.recall)="Grant on Delayed Recall"
        label(grant.recognition)="Grant on Delayed Recognition"
        label(bee.total)="Bee Total"
        label(bee.delayed.recall)="Bee on Delayed Recall"
        label(bee.recognition)="Bee on Delayed Recognition"
        label(plane.total)="Plane Total"
        label(plane.cued.recall)="Plane on Cued Recall"
        label(plane.delayed.recall)="Plane on Delayed Recall"
        label(plane.recognition)="Plane on Delayed Recognition"
        label(county.total)="County Total"
        label(county.cued.recall)="County on Cued Recall"
        label(county.delayed.recall)="County on Delayed Recall"
        label(county.recognition)="County on Delayed Recognition"
        label(choice.total)="Choice Total"
        label(choice.cued.recall)="Choice on Cued Recall"
        label(choice.delayed.recall)="Choice on Delayed Recall"
        label(choice.recognition)="Choice on Delayed Recognition"
        label(seed.total)="Seed Total"
        label(seed.cued.recall)="Seed on Cued Recall"
        label(seed.delayed.recall)="Seed on Delayed Recall"
        label(seed.recognition)="Seed on Delayed Recognition"
        label(wool.total)="Wool Total"
        label(wool.cued.recall)="Wool on Cued Recall"
        label(wool.delayed.recall)="Wool on Delayed Recall"
        label(wool.recognition)="Wool on Delayed Recognition"
        label(meal.total)="Meal Total"
        label(meal.cued.recall)="Meal on Cued Recall"
        label(meal.delayed.recall)="Meal on Delayed Recall"
        label(meal.recognition)="Meal on Delayed Recognition"
        label(srt.clean.entry.complete)="Complete?"
    })

    dat
}
